//import {Spinner} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Load from './Load';
import './Button.css'

function Button() {
    const [loading, setloading] = useState(false);
    const Change=()=>{
        setloading(false);
        setTimeout(()=>{
            setloading(false);

        }, 500);
}
if (loading) {
    return(
        <Load/>
    )
}else{
    return (
        <div>
             <input class="form-control" type="file" id="formFile"/>
                    <p></p>
                    <br/>
                    <span></span>
        <span></span>
        <span></span>
        <span></span>
             <a  className="env" onClick={()=>Change()}>Enviar</a>
        </div>

    )
}
}

export default Button
